// src/Home.js
import React from "react";

const galleryImages = [
  "/images/nohkalikai.jpg",
  "/images/kaziranga.jpg",
  "/images/weisawdong.jpg",
  "/images/dawki.jpg",
  "/images/guwahati zoo.jpg",
  "/images/cherrapunjee orchid resort.jpg",
  "/images/cherrapunjee.jpg",
  "/images/golfcourse shillong.jpg",
  "/images/sohra.jpg",
  "/images/dawki umngot river.jpg",
  "/images/umiam.jpg",
  "/images/umiam barapani.jpg",
  "/images/umngot river.jpg",
  "/images/weisawdong falls.jpg"
];

const tourPackages = [
  {
    name: "Shillong & Cherrapunjee Tour",
    days: "3D/2N",
    includes: "Sightseeing, cab, hotel"
  },
  {
    name: "Tawang & Dirang Adventure",
    days: "5D/4N",
    includes: "Stay, monastery visit, cab"
  },
  {
    name: "Dawki & Mawlynnong, Jowai Escape",
    days: "2D/1N",
    includes: "Cab, homestay, sightseeing"
  },
  {
    name: "Kamakhya and Umananda Darshan",
    days: "1D/1N",
    includes: "Cab, Hotel"
  },
  {
    name: "Kaziranga National Park",
    days: "2D/1N",
    includes: "Cabs, hotels, safari"
  }
];

export default function Home() {
  function handleWhatsApp() {
    const name = document.getElementById("visitorName").value;
    const phone = document.getElementById("visitorPhone").value;
    const message = document.getElementById("visitorMessage").value;

    const text = `Hi, I'm ${name}, my contact number is ${phone}. My message: ${message}`;
    const url = `https://wa.me/917005028948?text=${encodeURIComponent(text)}`;
    window.open(url, "_blank");
  }

  return (
    <div>
      <h1>Northeast Tour & Travels</h1>

      <h2>Gallery</h2>
      <div className="gallery">
        {galleryImages.map((src, index) => (
          <img
            key={index}
            src={src}
            alt={`Gallery ${index}`}
            style={{
              width: "300px",
              height: "200px",
              objectFit: "cover",
              borderRadius: "8px",
              margin: "10px"
            }}
          />
        ))}
      </div>

      <h2 style={{ marginTop: "40px" }}>Tour Packages</h2>
      <div className="tour-packages">
        {tourPackages.map((pkg, index) => (
          <div className="tour-card" key={index}>
            <h3>{pkg.name}</h3>
            <p><strong>Duration:</strong> {pkg.days}</p>
            <p><strong>Includes:</strong> {pkg.includes}</p>
            <a
              className="whatsapp-btn"
              href={`https://wa.me/917005028948?text=${encodeURIComponent(`Hi, I want to enquire about the ${pkg.name} package.`)}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              Enquire on WhatsApp
            </a>
          </div>
        ))}
      </div>

      <h2>Contact Us</h2>
      <div className="contact-form">
        <input type="text" id="visitorName" placeholder="Your Name" required /><br />
        <input type="text" id="visitorPhone" placeholder="Your Contact Number" required /><br />
        <textarea id="visitorMessage" placeholder="Your Message" required></textarea><br />
        <button type="button" onClick={handleWhatsApp}>Send via WhatsApp</button>
      </div>
    </div>
  );
}
