import React from "react";
import ReactDOM from "react-dom/client";

const galleryImages = [
  "/images/nohkalikai.jpg",
  "/images/kaziranga.jpg",
  "/images/weisawdong.jpg"
];


const tourPackages = [
  {
    name: "Kaziranga Wildlife Safari",
    days: "2D/1N",
    price: "₹6,000",
    includes: "Safari + Hotel + Breakfast"
  },
  {
    name: "Shillong & Cherrapunjee",
    days: "4D/3N",
    price: "₹9,500",
    includes: "Sightseeing + Cab + Hotel"
  },
  {
    name: "Tawang Adventure",
    days: "5D/4N",
    price: "₹14,000",
    includes: "Hotel + Travel + Monastery Visit"
  }
];

const App = () => {
  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Northeast Tour & Travels</h1>

      <h2>Gallery</h2>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
          gap: "20px",
          marginBottom: "40px"
        }}
      >
        {galleryImages.map((url, index) => (
          <img
            key={index}
            src={url}
            alt={`Gallery ${index}`}
            style={{ width: "100%", borderRadius: "10px" }}
          />
        ))}
      </div>

      <h2>Tour Packages</h2>
      <div style={{ display: "grid", gap: "20px", marginBottom: "40px" }}>
        {tourPackages.map((pkg, index) => (
          <div
            key={index}
            style={{
              border: "1px solid #ccc",
              padding: "15px",
              borderRadius: "10px"
            }}
          >
            <h3>{pkg.name}</h3>
            <p><strong>Duration:</strong> {pkg.days}</p>
            <p><strong>Price:</strong> {pkg.price}</p>
            <p><strong>Includes:</strong> {pkg.includes}</p>
          </div>
        ))}
      </div>

      <h2>Contact Us</h2>
      <form
        action="https://formspree.io/f/your-form-id"
        method="POST"
        style={{
          display: "flex",
          flexDirection: "column",
          gap: "10px",
          maxWidth: "400px"
        }}
      >
        <input type="text" name="name" placeholder="Your Name" required />
        <input type="email" name="email" placeholder="Your Email" required />
        <textarea name="message" placeholder="Your Message" required />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
