import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

const galleryImages = [
  "/images/nohkalikai.jpg",
  "/images/kaziranga.jpg",
  "/images/weisawdong.jpg",
  "/images/dawki.jpg",
  "/images/guwahati zoo.jpg",
  "/images/cherrapunjee orchid resort.jpg",
  "/images/cherrapunjee.jpg",
  "/images/golfcourse shillong.jpg",
  "/images/sohra.jpg",
  "/images/dawki umngot river.jpg",
  "/images/umiam.jpg",
  "/images/umiam barapani.jpg",
  "/images/umngot river.jpg",
  "/images/weisawdong falls.jpg"
];

const tourPackages = [
  {
    name: "Kaziranga Wildlife Safari",
    days: "2D/1N",
    price: "₹6,000",
    includes: "Safari + Hotel + Breakfast"
  },
  {
    name: "Shillong & Cherrapunjee",
    days: "4D/3N",
    price: "₹9,500",
    includes: "Sightseeing + Cab + Hotel"
  },
  {
    name: "Tawang Adventure",
    days: "5D/4N",
    price: "₹14,000",
    includes: "Hotel + Travel + Monastery Visit"
  }
];

function App() {
  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Northeast Tour & Travels</h1>

      <h2>Gallery</h2>
      <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }} className="gallery">
        {galleryImages.map((src, index) => (
          <div key={index}>
            <img
              src={src}
              alt={`Gallery ${index}`}
              style={{
                width: "300px",
                height: "200px",
                objectFit: "cover",
                borderRadius: "8px",
                border: "1px solid #ccc"
              }}
            />
          </div>
        ))}
      </div>

      <h2 style={{ marginTop: "40px" }}>Tour Packages</h2>
      {tourPackages.map((pkg, index) => (
        <div
          key={index}
          style={{
            border: "1px solid #ccc",
            padding: "15px",
            marginBottom: "20px",
            borderRadius: "8px",
            maxWidth: "600px"
          }}
        >
          <h3>{pkg.name}</h3>
          <p><strong>Duration:</strong> {pkg.days}</p>
          <p><strong>Price:</strong> {pkg.price}</p>
          <p><strong>Includes:</strong> {pkg.includes}</p>
        </div>
      ))}

      <h2>Contact Us</h2>
      <form className="contact-form">
        <input type="text" placeholder="Your Name" style={inputStyle} /><br />
        <input type="email" placeholder="Your Email" style={inputStyle} /><br />
        <textarea placeholder="Your Message" style={inputStyle}></textarea><br />
        <button type="submit" style={buttonStyle}>Send</button>
      </form>
    </div>
  );
}

const inputStyle = {
  margin: "5px 0",
  padding: "10px",
  width: "100%",
  maxWidth: "400px"
};

const buttonStyle = {
  padding: "10px 20px",
  background: "#007bff",
  color: "white",
  border: "none",
  cursor: "pointer",
  borderRadius: "5px"
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
